import React, { useState } from "react";
import "./styles.css";

function Navbar() {
  const [active, setActive] = useState("nav__menu");
  const [icon, setIcon] = useState("nav__toggler");

  const navToggle = () => {
    // Toggle the navigation menu
    if (active === "nav__menu") {
      setActive("nav__menu nav__active");
    } else {
      setActive("nav__menu");
    }

    // Toggle the hamburger icon
    if (icon === "nav__toggler") {
      setIcon("nav__toggler toggle");
    } else {
      setIcon("nav__toggler");
    }
  };

  // Function to scroll to a specific section when a navigation link is clicked
  const scrollToSection = (sectionId) => {
    // Close the navigation menu when a link is clicked
    setActive("nav__menu");
    setIcon("nav__toggler");

    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <nav className="nav">
      <a href="/" className="nav__brand">
        <img src="/images/LOGO.png" alt="img" />
      </a>
      <ul data-testid="nav-menu" className={active}>
        <li className="nav__item">
          <a href="#home" className="nav__link" onClick={() => scrollToSection("home")}>
            Home
          </a>
        </li>
        <li className="nav__item">
          <a href="#product" className="nav__link" onClick={() => scrollToSection("product")}>
            About
          </a>
        </li>
        <li className="nav__item">
          <a href="#team" className="nav__link" onClick={() => scrollToSection("team")}>
            Team
          </a>
        </li>
        <li className="nav__item">
          <a href="#footer" className="nav__link" onClick={() => scrollToSection("footer")}>
            Contact
          </a>
        </li>
        <li className="nav__item">
          <a href="" className="nav__link">
            <button>Register</button>
          </a>
        </li>
      </ul>
      <div data-testid="nav-toggler" onClick={navToggle} className={icon}>
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </nav>
  );
}

export default Navbar;
